schema:
{
    "type": "object",
    "properties": {
        "searchMode": {
            "type": "string",
            "label": "Search mode",
            "enum": ["Substructure", "Exact structure", "Similarity"],
            "default": "Substructure",
            "required": true
        },
        "name": {
            "type": "string",
            "label": "Filter by article name",
            "default": ""
        }
    }
}

script:
var link = this.variables.link.get() + '';

return agent.get(link).end().then(function (res) {
    // Replace protocol relative urls with absolute protocol
    // because it does not work with blob urls
    var html = res.text.replace(/"\/\//g, '"https://');
    var blob = new Blob([html], {type : 'text/html'});
    var url = URL.createObjectURL(blob);
    set('url', url);
    done();
});

require(['src/util/api', 'openchemlib/openchemlib-core'], function (API, OCL) {
    var hover = API.getData('hover');
    var act;
    if (hover && (act = hover.get('actID'))) {
        var mol = OCL.Molecule.fromIDCode(act.value + '', act.coordinates + '').toMolfile();
        API.createData('customQueryOpts', {searchMode: 'Similarity'});
        API.createData('queryOptions', {searchMode: 'Similarity'});
        API.createData('selectedMol', {type: 'mol2d', value: mol});
        API.createData('query', {type: 'mol2d', value: mol});
    }
});

require(['src/util/api'], function (API) {
    var currentDrug = API.getData('hover');
    if (currentDrug) {
        var link = currentDrug.get('wiki').get() + '';
        window.open(link, '_blank')
    }
});

require(['uri/URI'], function (URI) {
    var url = new URI(window.location.href);
    url.fragment(encodeURIComponent(value));
    window.history.pushState(null, '', url.href());
});

require([
    'src/util/api',
    'src/util/debug',
    'openchemlib//openchemlib-core',
    'components/async/lib/async',
    'uri/URI'
], function (API, Debug, OCL, async, URI) {
    if (API.cache('db')) {
        return;
    }

    var url = new URI(window.location.href);

    function getName(value) {
        value = value.replace(/_/g, ' ');
        value = decodeURIComponent(value);
        return value;
    }

    function toObj(val) {
        return {id: val};
    }

    var errors = API.getData('errors');

    var fullA = API.getData('fullA');
    var full = fullA.map(toObj);

    var el = errors.length;
    var hid = 0;

    for (var k = 0; k < el; k++) {
        var id = errors[k].id;
        var idx = fullA.indexOf(id);
        if (idx > -1) {
            var error = full[idx];
            if (!error._highlight) {
                error._highlight = [++hid];
            } else {
                error._highlight.push(++hid);
            }
            errors[k]._highlight = [hid];
        } else {
            errors[k]._highlight = [];
        }
    }

    API.createData('full', full);
    API.createData('dup', API.getData('dupA').map(toObj));
    API.createData('notfound', API.getData('notfoundA').map(toObj));

    var molecules = API.getData('molecules');
    if (molecules && molecules.length) {
        // ?mini=true => only 500 molecules for testing
        var l = molecules.length;
        if(document.location.search.indexOf('mini') > -1) {
            l = Math.min(l, 500);
        }
        var db = new Array(l);
        API.cache('db', db);
        var timer = Debug.timer();
        var i = 0, ii, molecule;

        async.whilst(function () {
            ii = Math.min(i + 1000, l);

            for (; i < ii; i++) {
                API.loading('mol', 'Loading molecules (' + (i + 1) + '/' + l + ')');
                molecule = molecules[i];
                // we will add the links in the molecules
                molecule.link = 'https://en.wikipedia.org/api/rest_v1/page/html/' + molecule.code;
                molecule.wiki = 'https://en.wikipedia.org/wiki/' + molecule.code;
                molecule.name = getName(molecule.code);
                molecule.lowName = molecule.name.toLowerCase();
                var newEntry = {};
                newEntry.molecule = OCL.Molecule.fromIDCode(molecule.actID.value, false);
                newEntry.molecule.ensureHelperArrays(3);
                newEntry.index = i;
                db[i] = newEntry;
            }

            return ii !== l
        }, function (callback) {
            setImmediate(callback);
        }, function () {
            Debug.debug(timer.time('ms'));
            var fragment = decodeURIComponent(url.fragment());
            var query = {
                type: 'mol2d',
                value: ''
            };
            if (fragment) {
                try {
                    var mol = OCL.Molecule.fromSmiles(fragment);
                    var molfile = mol.toMolfile();
                    query.value = molfile;
                    API.createData('selectedMol', {type: 'mol2d', value: molfile});
                    API.createData('customQueryOpts', {searchMode: 'Similarity'});
                    API.createData('queryOptions', {searchMode: 'Similarity'});
                } catch (e) {}
            } else {
                API.createData('customQueryOpts', {searchMode: 'Substructure'});
                API.createData('queryOptions',{searchMode: 'Substructure'});
            }
            API.createData('query', query, 'searchFilter');
            API.stopLoading('mol');
        });

    }
});

return {
    filter: function (value, resolve, reject) {

        resolve(value);

        var molfile, options;
        if (value.__name === 'query') {
            molfile = value;
            options = API.getData('queryOptions');
        } else if (value.__name === 'queryOptions') {
            options = value;
            molfile = API.getData('query');
        }

        if (!options) {
            return;
        }

        var molecules = API.getData('molecules');

        var result = [],
            i, ii;

        var queryMol;
        if (molfile) {
            try {
                queryMol = OCL.Molecule.fromMolfile(molfile.get() + '');
            } catch (e) {}
        }

        if (!options || !queryMol || queryMol.getAllAtoms() === 0) {
            for (i = 0, ii = molecules.length; i < ii; i++) {
                result.push(molecules[i]);
            }
            return sendResult(result);
        }

        var mode = String(options.get('searchMode'));
        var targetMW;

        switch (mode) {
            case 'Exact structure':
            {
                var idcode = queryMol.getIDCode();
                for (i = 0, ii = molecules.length; i < ii; i++) {
                    if (String(molecules[i].actID.value) === idcode) {
                        result = [molecules[i]];
                        break;
                    }
                }
                break;
            }
            case 'Substructure':
            {
                targetMW = queryMol.getMolecularFormula().relativeWeight;
                queryMol.setFragment(true);
                var queryIndex = queryMol.getIndex();
                var searcher = new OCL.SSSearcherWithIndex();
                searcher.setFragment(queryMol, queryIndex);
                var db = API.cache('db');
                for (i = 0, ii = db.length; i < ii; i++) {
                    var actmol = db[i].molecule,
                        mol = molecules[db[i].index];
                    searcher.setMolecule(actmol, mol.act_idx);
                    if (searcher.isFragmentInMolecule()) {
                        result.push(mol);
                    }
                }
                result.sort(function (a, b) {
                    return Math.abs(targetMW - a.mw) - Math.abs(targetMW - b.mw);
                });

                break;
            }
            case 'Similarity':
            {
                var index = queryMol.getIndex();
                targetMW = queryMol.getMolecularFormula().relativeWeight;
                var targetID = queryMol.getIDCode();
                var intermediate = [];
                var similarity;
                for (i = 0, ii = molecules.length; i < ii; i++) {
                    if (String(molecules[i].actID.value) === targetID) {
                        similarity = 1e10;
                    } else {
                        similarity = OCL.SSSearcherWithIndex.getSimilarityTanimoto(index, molecules[i].act_idx) * 100000 - Math.abs(targetMW - molecules[i].mw) / 1000;
                    }
                    intermediate.push([molecules[i], similarity]);
                }
                intermediate.sort(function (a, b) {
                    return b[1] - a[1];
                });

                for (i = 0, ii = intermediate.length; i < ii; i++) {
                    result.push(intermediate[i][0]);
                }

                break;
            }
        }

        sendResult(result);

        API.setVar('hover', ['searchResult', 0]);

        function sendResult(result) {
            var name = String(options.get('name')).toLowerCase();
            if (name != 'undefined') { // filter by name
                var oldResult = result;
                result = [];
                for (i = 0, ii = oldResult.length; i < ii; i++) {
                    if (oldResult[i].lowName.indexOf(name) > -1) {
                        result.push(oldResult[i]);
                    }
                }
            }

            // if there is some result we change the link, otherwise we blank it
            if (result.length > 0) {
                API.createData('link', result[0].link + '');
            } else {
                API.createData('link', null);
            }

            API.createData('searchResult', result);
        }

    }
};
